import React from 'react';
import {Link} from 'react-router-dom';
import * as BooksAPI from './BooksAPI';

class ShelfChanger extends React.Component
{
    updateShelf=(book, shelf)=>
        {
            BooksAPI.update(book, shelf)
                // .then((response)=>console.log(response))
        }

    render()
    {
        return(
            <div className="book-shelf-changer">
                <select>
                    <option value="move" disabled>Move to...</option>
                    <option value="currentlyReading" on ={this.updateShelf(this.props.book, 'currentlyReading')}>Currently Reading</option>
                    <option value="wantToRead" onClick={this.updateShelf(this.props.book, 'wantToRead')}>Want to Read</option> 
                    <option value="read" onClick={this.updateShelf(this.props.book, 'read')}>Read</option>
                    <option value="none">None</option>
                </select>
            </div>
        )
    }
}

export default ShelfChanger;
