import React from 'react'
import * as BooksAPI from './BooksAPI'
import './App.css'
import {Route, Redirect, withRouter, Link} from 'react-router-dom';
import SearchLibrary from './SearchLibrary';
import Shelf from './Shelf';
import ShelfChanger from './ShelfChanger';

class BooksApp extends React.Component {
  state = {
    books: [],
    currentlyReading: [],
    wantToRead: [],
    read: []
  }
  componentDidMount()
  {
    BooksAPI.getAll()
      .then((response)=>this.setState({books:response}))
      .then(()=>{
        this.state.books.forEach((book) => {
          switch(book.shelf)
          {
            case 'currentlyReading': {this.setState(currentState => ({currentlyReading: currentState.currentlyReading.concat([book]) }))}
                                      break;
            case 'wantToRead': {this.setState(currentState => ({wantToRead: currentState.wantToRead.concat([book]) }))}
                                      break;    
            case 'read': {this.setState(currentState => ({read: currentState.read.concat([book]) }))}
                                      break;    
            default:               
          }
        })
      })
      // .then(()=>console.log(this.state.currentlyReading))
  }

  
  addToShelf(book, shelf)
  {
    let bookToBeUpdated=this.state.book.filter(book=>
      book.id===book.id)
    bookToBeUpdated.shelf=shelf
  }

  render() 
  {
      return (
      <div className="app">
          <div className="list-books">
            <div className="list-books-title">
              <h1>MyReads</h1>
            </div>
            <div className="list-books-content">
              <div>
                <Route exact path='/'>
                  <Shelf shelf={this.state.currentlyReading} currentShelf={'currentlyReading'} name={'Currently Reading'}/>
                  <Shelf shelf={this.state.wantToRead} currentShelf={'wantToRead'} name={'Want To Read'}/>
                  <Shelf shelf={this.state.read} currentShelf={'read'} name={'Read'}/>
                </Route>
                {this.state.book[0]!==undefined && 
                    console.log(this.state.book)
                  }
              </div>
            </div>            
          </div>  

          <div className="open-search">
              <Link to='/search'>
                <button>Add a book</button>
              </Link>            
          </div>     
          <Route exact path='/search' component={SearchLibrary}/>
            {/* <SearchLibrary/>
          </Route>  */}
      </div>
    )
  }
}
export default BooksApp
// export default withRouter(BooksApp)
