import React from 'react'
import * as BooksAPI from './BooksAPI'
import './App.css'
import {Route, Link} from 'react-router-dom';
import SearchLibrary from './SearchLibrary';
import Shelf from './Shelf';

class BooksApp extends React.Component {
  state = {  
    loading: true,
    queryResult: [],
    books: [],
    currentlyReading: [],
    wantToRead: [],
    read: []

  }
  componentDidMount()
  {
    BooksAPI.getAll()
      .then((response)=>this.setState({books:response}))
      .then(()=>{
        this.state.books.forEach((book) => {
          switch(book.shelf)
          {
            case 'currentlyReading': 
            {this.setState(currentState => ({currentlyReading: currentState.currentlyReading.concat([book]) }))}
                                      break;
            case 'wantToRead': {this.state.wantToRead.push({book})}
                                      break;    
            case 'read': {this.state.read.push({book})}
                                      break;    
            default:               
          }
        })
      }, this.setState({ loading: false }))
      // .then(()=>console.log(this.state.currentlyReading))
  }

  
  addToShelf(book, shelf)
  {}

  render() {

    if(this.state.loading) 
    {
      return (
        <div>
          {console.log('Loading')}
          <p>Loading, please wait</p>
        </div>
      );
    }
    return (
      <div className="app">
          <div className="list-books">
            <div className="list-books-title">
              <h1>MyReads</h1>
            </div>
            <div className="list-books-content">
              <div>
                {/* <Shelf shelf={this.state.currentlyReading}/>
                <Shelf shelf={this.state.wantToRead}/>
                <Shelf shelf={this.state.read}/>*/}
                {console.log(this.state.books)} 
              </div>
            </div>
            <div className="open-search">
                <button onClick={()=> <Route path='/search' render={()=><SearchLibrary/>}/>}></button>
            </div>
          </div>
      </div>
    )
  }
}

export default BooksApp

